import React from 'react';

export default function About() {
    document.title = "Homepage - DreamVortex";
    const getStyles = () => {
        return {
            container: {
                maxWidth: '1200px',
                margin: '0 auto',
                padding: '20px',
                marginTop: '70px',
                fontFamily: 'Arial, sans-serif',
            },
            heading: {
                fontSize: '30px',
                fontWeight: 'bold',
                marginBottom: '7px',
                marginTop: '7px',
                color: '#333',
                textAlign: 'center',
                textTransform: 'uppercase',
            },
            subHeading: {
                fontSize: '24px',
                fontWeight: 'bold',
                marginTop: '40px',
                marginBottom: '10px',
                color: '#333',
            },
            paragraph: {
                fontSize: '18px',
                marginBottom: '15px',
                lineHeight: '1.6',
                color: '#333',
            },
            list: {
                listStyleType: 'circle',
                paddingLeft: '20px',
                fontSize: '18px',
                marginBottom: '15px',
                color: '#333',
            },
            thankYou: {
                marginTop: '40px',
                fontStyle: 'italic',
                color: '#333'
            }
        };
    }

    const styles = getStyles();

    return (
        <div style={styles.container}>
            <h1 className="text-center" style={styles.heading}>Dream Vortex - Realm of Infinite Imaginations</h1>
            <h2 style={styles.subHeading}>Our Vision</h2>
            <p style={styles.paragraph}>Deep Dream Creations is a platform that enables users to upload their photos and apply customized Deep Dream effects. Our vision is to provide a seamless and creative experience where users can explore endless artistic possibilities.</p>

            <h2 style={styles.subHeading}>Key Features</h2>
            <p style={styles.paragraph}>Discover the exciting features of Deep Dream Creations:</p>
            <ul style={styles.list}>
                <li style={styles.list}>Upload Your Photos: Seamlessly upload your photos to the platform and apply unique Deep Dream effects.</li>
                <li style={styles.list}>Customize Effects: Choose from a range of styles and parameters to create stunning and personalized artworks.</li>
                <li style={styles.list}>Purchase Options: Explore options to purchase prints or digital copies of your creations, making it easy to showcase and share your artwork.</li>
                <li style={styles.list}>Community Engagement: Connect with a community of artists, share your creations, and discover inspiration from others.</li>
            </ul>

            <h2 style={styles.subHeading}>Explore Endless Creativity</h2>
            <p style={styles.paragraph}>Deep Dream Creations offers a dynamic platform for artistic exploration:</p>
            <ul style={styles.list}>
                <li style={styles.list}><strong>Style Variety:</strong> Choose from a diverse range of Deep Dream styles to create unique and captivating artworks.</li>
                <li style={styles.list}><strong>Parameter Control:</strong> Adjust parameters such as intensity, color palette, and dream-like distortions to achieve your desired artistic expression.</li>
                <li style={styles.list}><strong>Artwork Purchase:</strong> Purchase prints or digital copies of your creations to showcase your artwork in various formats.</li>
                <li style={styles.list}><strong>Artistic Community:</strong> Engage with fellow artists, share techniques, and explore new ideas within a supportive community.</li>
            </ul>

            <h2 style={styles.subHeading}>Join the Deep Dream Revolution</h2>
            <p style={styles.paragraph}>Experience the fusion of art and technology with Deep Dream Creations. Unleash your imagination, create captivating artworks, and embark on a journey of artistic discovery.</p>

            <p style={styles.thankYou}>Thank you for joining us on this creative journey with Deep Dream Creations. Let&apos;s explore and celebrate the limitless possibilities of digital artistry together.</p>
        </div>
    );
}
