import React, { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Dream() {
    const [Image1, setImage1] = useState(null);
    const [Image2, setImage2] = useState(null);
    const [preview1, setPreview1] = useState(null);
    const [preview2, setPreview2] = useState(null);
    const [final, setFinal] = useState(null);
    const [spin, setSpin] = useState(false);
    const [alert, setAlert] = useState("");
    const fileInputRef1 = useRef(null);
    const fileInputRef2 = useRef(null);
    document.title = "Creativity - DreamVortex";

    const handleImageUpload = async () => {
        try {
            setSpin(true);
            if (!Image1 || !Image2) {
                console.log('Please upload both images');
                setAlert("Please Upload the Images");
                setTimeout(() => {
                    setAlert("");
                }, 2000);
                setSpin(false);
                return;
            }
            const formData = new FormData();
            formData.append('file1', Image1);
            formData.append('file2', Image2);

            const response = await fetch('http://localhost:5500/upload', {
                method: 'POST',
                body: formData
            });

            const result = await response.json();

            if (response.ok) {
                console.log('Images uploaded:', result.message);
                setImage1(null);
                setImage2(null);
                setFinal(result.imageUrl);
                setSpin(false);
                fileInputRef1.current.value = '';
                fileInputRef2.current.value = '';
                setPreview1(null);
                setPreview2(null);
            } else {
                console.error('Upload failed:', result.error);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleImageUpload1 = (event) => {
        const file = event.target.files[0];
        setImage1(file);
        if (file) {
            const objectUrl = URL.createObjectURL(file);
            setPreview1(objectUrl);
        } else {
            setPreview1(null);
        }
    };

    const handleImageUpload2 = (event) => {
        const file = event.target.files[0];
        setImage2(file);
        if (file) {
            const objectUrl = URL.createObjectURL(file);
            setPreview2(objectUrl);
        } else {
            setPreview2(null);
        }
    };

    const handleImageDeload1 = () => {
        setImage1(null);
        setPreview1(null);
        fileInputRef1.current.value = '';
    };

    const handleImageDeload2 = () => {
        setImage2(null);
        setPreview2(null);
        fileInputRef2.current.value = '';
    };

    const handleRemove = () => {
        setFinal(null);
    };

    const handleDownload = async () => {
        if (final) {
            try {
                const response = await fetch(`http://localhost:5500${final}`);
                const blob = await response.blob();

                const downloadLink = document.createElement('a');
                downloadLink.href = window.URL.createObjectURL(blob);
                downloadLink.download = 'Styled_Image.jpg';
                downloadLink.click();
            } catch (error) {
                console.error('Download error:', error);
            }
        }
    };

    return (
        <div className="container" style={{ marginTop: "80px" }}>
            {alert && <div className="alert alert-danger text-center" role="alert">{alert}</div>}
            <h1 className="text-center">DreamVortex Image Personalization</h1>
            <p className="text-center" style={{ fontSize: '1.2rem', color: '#555', marginTop: '20px' }}>Upload two images below to start your dream journey!</p>
            <div className="d-flex justify-content-center">
                <div className="custom-file" style={{ marginRight: "10px" }}>
                    <label htmlFor="ContentImage" style={{ fontSize: '18px', fontWeight: 'bold', color: '#333', display: 'block', marginBottom: "10px" }}>ðŸ“¸ Content Image:</label>
                    <input
                        type="file" onChange={handleImageUpload1} style={{ color: "blue" }} ref={fileInputRef1} accept='image/*'
                        className="form-control"
                    />
                    {
                        preview1 && (
                            <div className="mt-2">
                                <img src={preview1} alt="Preview 1" className="img-thumbnail" style={{ width: '150px', marginRight: "10px" }} />
                                <button className="btn btn-danger mt-2" onClick={handleImageDeload1}>Delete</button>
                            </div>
                        )
                    }
                </div >
                <div className="custom-file" style={{ marginLeft: "10px" }}>
                    <label htmlFor="StyleImage" style={{ fontSize: '18px', fontWeight: 'bold', color: '#333', display: 'block', marginBottom: "10px" }}>ðŸŽ¨ Style Image:</label>
                    <input
                        type="file" onChange={handleImageUpload2} style={{ color: "blue" }} ref={fileInputRef2} accept='image/*'
                        className="form-control"
                    />
                    {preview2 && (
                        <div className="mt-2">
                            <img src={preview2} alt="Preview 2" className="img-thumbnail" style={{ width: '150px', marginRight: "10px" }} />
                            <button className="btn btn-danger mt-2" onClick={handleImageDeload2}>Delete</button>
                        </div>
                    )}
                </div>
            </div >
            <div className="text-center" style={{ marginTop: "18px" }}>
                {!alert && spin && <div className="spinner-border text-primary" role="status"></div>}
                {!spin && !final && <button className="btn btn-success" onClick={handleImageUpload}>Upload Images</button>}
            </div>
            {
                final && (
                    <div className='text-center'>
                        <div className="text-center mt-4">
                            <h3>Stylized Image</h3>
                            <img src={`http://localhost:5500${final}`} alt="Stylized" className="img-thumbnail" style={{ width: '300px' }} />
                        </div>
                        <button className="btn btn-success" style={{ marginTop: "15px" }} onClick={handleDownload}>Download</button>
                        <button className="btn btn-success" style={{ marginTop: "15px", marginLeft: "7px" }} onClick={handleRemove}>Close</button>
                    </div>
                )
            }
        </div >
    );
}
