import React, { useState } from 'react';

export default function Dalle() {
    const [inputText, setInputText] = useState('');

    const handleInputChange = (event) => {
        setInputText(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const response = await fetch('https://localhost:5500/imageconv', {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
            },
            body: { text: inputText }
        })
        const data = await response.json();
        if (data.ok) {
            console.log(inputText);
        }
        else {
            console.log(data.error);
        }
        setInputText('');
    };

    return (
        <div className="container" style={{ padding: '20px', maxWidth: '600px', marginTop: "70px" }}>
            <h1 className='text-center'>DreamVortex Image Generator</h1>
            <p className='text-center' style={{ fontSize: '1.2rem', color: '#555', marginTop: '20px' }}>
                Welcome to the Dalle Text Input page! Enter some text in the input field below and click the submit button
                to perform actions with the text.
            </p>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="textInput" style={{ fontSize: '1.2rem', color: '#555', marginTop: '20px', marginBottom: "10px" }}>Enter Text:</label>
                    <input
                        type="text"
                        id="textInput"
                        value={inputText}
                        onChange={handleInputChange}
                        placeholder="Type your text here"
                        className="form-control"
                    />
                </div>
            </form>
            {inputText && (
                <div style={{ marginTop: '20px' }}>
                    <h3>Input Text:</h3>
                    <p>{inputText}</p>
                </div>
            )}
        </div>
    );
}
