import React, { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Dream() {
    const [uploadedImage, setUploadedImage] = useState(null);
    const [imageId, setImageId] = useState(null);
    const fileInputRef = useRef(null);

    document.title = "Creativity - DreamVortex";

    const handleImageUpload = async (event) => {
        const file = event.target.files[0];
        if (file) {
            const formData = new FormData();
            formData.append('file', file);

            try {
                const response = await fetch('http://localhost:5500/upload', {
                    method: 'POST',
                    body: formData
                });

                const result = await response.json();
                if (response.ok) {
                    setImageId(result.imageId);
                    setUploadedImage(URL.createObjectURL(file));
                } else {
                    console.error('Upload failed:', result.error);
                }
            } catch (error) {
                console.error('Error:', error);
            }
        }
    };

    const handleImageDeload = () => {
        setUploadedImage(null);
        setImageId(null);
        fileInputRef.current.value = '';
    };

    return (
        <div className="container" style={{ marginTop: "100px" }}>
            <h1 className="text-center">DreamVortex Image Personalization</h1>
            <p className="text-center" style={{ fontSize: '1.2rem', color: '#555', marginTop: '20px' }}>Upload an image below to start your dream journey!</p>
            <div className="custom-file" style={{ marginBottom: "10px" }}>
                <input
                    type="file"
                    onChange={handleImageUpload}
                    style={{ marginLeft: "20px", marginRight: "20px" }}
                    ref={fileInputRef}
                    accept='image/*'
                />
                <input
                    type="file"
                    onChange={handleImageUpload}
                    style={{ marginLeft: "20px", marginRight: "20px" }}
                    ref={fileInputRef}
                    accept='image/*'
                />
                <button className="btn btn-primary" onClick={handleImageDeload}>Delete</button>
            </div>
            {
                uploadedImage && (
                    <div className="text-center" style={{ marginTop: "30px" }}>
                        <h3 className="mb-3">Uploaded Image Preview</h3>
                        <img src={uploadedImage} alt="Uploaded" className="img-fluid" style={{ maxWidth: '100%' }} />
                    </div>
                )
            }
        </div>
    );
}
