import React, { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Dream() {
    const [uploadedImage, setUploadedImage] = useState(null);
    const fileInputRef = useRef(null);
    document.title = "Creativity - DreamVortex";
    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setUploadedImage(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleImageDeload = () => {
        setUploadedImage(null);
        fileInputRef.current.value = '';
    };

    return (
        <div className="container" style={{ marginTop: "80px" }}>
            <h1 className="text-center">DreamVortex Image Personalization</h1>
            <p className="text-center" style={{ fontSize: '1.2rem', color: '#555', marginTop: '20px' }}>Upload an image below to start your dream journey!</p>
            <div className="custom-file" style={{ marginLeft: "450px" }}>
                <input
                    type="file" onChange={handleImageUpload} style={{ marginLeft: "10px", marginRight: "10px", borderRadius: "20px" }} ref={fileInputRef} accept='image/*'
                />
                <button className="btn btn-primary" onClick={handleImageDeload}>Delete</button>
            </div>
            {
                uploadedImage && (
                    <div className="text-center" style={{ marginTop: "30px" }}>
                        <h3 className="mb-3">Uploaded Image Preview</h3>
                        <img src={uploadedImage} alt="Uploaded" className="img-fluid" style={{ maxWidth: '100%' }} />
                    </div>
                )
            }
        </div >
    );
}
