import React, { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Dream() {
    const [Image, setImage] = useState(null);
    const [preview, setpreview] = useState(null);
    const [final, setFinal] = useState(null);
    const [spin, setSpin] = useState(false);
    const [alert, setAlert] = useState("");
    const fileInputRef = useRef(null);
    document.title = "Creativity - DreamVortex";

    const ImageUpload = async () => {
        try {
            setSpin(true);
            if (!Image) {
                console.log('Please Upload the Image');
                setAlert("Please Upload the Image");
                setTimeout(() => {
                    setAlert("");
                }, 2000);
                setSpin(false);
                return;
            }
            const formData = new FormData();
            formData.append('file', Image);

            const response = await fetch('http://localhost:5500/dd', {
                method: 'POST',
                body: formData
            });

            const result = await response.json();

            if (response.ok) {
                console.log('Images uploaded:', result.message);
                setImage(null);
                setFinal(result.imageUrl);
                setSpin(false);
                fileInputRef.current.value = '';
                setpreview(null);
            } else {
                setSpin(false);
                console.error('Upload failed:', result.error);
            }
        } catch (error) {
            setSpin(false);
            console.error('Error:', error);
        }
    };

    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        setImage(file);
        if (file) {
            const objectUrl = URL.createObjectURL(file);
            setpreview(objectUrl);
        } else {
            setpreview(null);
        }
    };

    const handleImageDeload = () => {
        setImage(null);
        setSpin(false);
        setpreview(null);
        fileInputRef.current.value = '';
    };

    const handleRemove = () => {
        setFinal(null);
    };

    const handleDownload = async () => {
        if (final) {
            try {
                const response = await fetch(`http://localhost:5500${final}`);
                const blob = await response.blob();

                const downloadLink = document.createElement('a');
                downloadLink.href = window.URL.createObjectURL(blob);
                downloadLink.download = 'Styled_Image.jpg';
                downloadLink.click();
            } catch (error) {
                console.error('Download error:', error);
            }
        }
    };

    return (
        <div style={{
            backgroundImage: `url(${require('../DeepDream.png')})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            height: '100vh',
            margin: 0,
            backgroundRepeat: 'no-repeat',
            backgroundAttachment: 'fixed'
        }}>
            <div className="container" style={{ marginTop: "80px" }}>
                {alert && <div className="alert alert-danger text-center" role="alert" style={{ height: "50px", backgroundColor: "#f8d7da", color: "#721c24", border: "1px solid #f5c6cb", borderRadius: "5px" }}>{alert}</div>}
                <h2 className="text-center">Dreamy Creations through Deep Dream Alchemy</h2>
                <p className="text-center" style={{ fontSize: '1.2rem', color: '#555', marginTop: '15px' }}>Upload two images below to start your dream journey!</p>
                <div className="d-flex justify-content-center">
                    <div className="custom-file text-center" style={{ marginRight: "10px" }}>
                        <label htmlFor="ContentImage" style={{ fontSize: '18px', fontWeight: 'bold', color: '#333', display: 'block', marginBottom: "10px", marginRight: "20px" }}>ðŸ’¤ Dreaming Image:</label>
                        <input
                            type="file" onChange={handleImageUpload} style={{ color: "blue" }} ref={fileInputRef} accept='image/*'
                            className="form-control"
                        />
                        {
                            preview && (
                                <div className="mt-2 text-center">
                                    <img src={preview} alt="Preview" className="img-thumbnail" style={{ width: '175px', height: "175px", marginTop: "10px" }} />
                                </div>
                            )
                        }
                        <div style={{ justifyContent: "space-evenly", display: "flex" }}>
                            {preview && <button className="btn btn-danger" onClick={handleImageDeload} style={{ marginTop: "15px" }}>Delete</button>}
                            {!spin && !final && <button className="btn btn-success" onClick={ImageUpload} style={{ marginTop: "15px" }} >Upload</button>}
                        </div>
                        {!alert && spin && <div className="spinner-border text-success text-center" style={{ marginTop: "20px" }} role="status"></div>}
                    </div >
                </div >
                {
                    final && (
                        <div className='text-center'>
                            <div className="text-center mt-4">
                                <h3>Stylized Image</h3>
                                <img src={`http://localhost:5500${final}`} alt="Stylized" className="img-thumbnail" style={{ width: '300px' }} />
                            </div>
                            <button className="btn btn-success" style={{ marginTop: "15px" }} onClick={handleDownload}>Download</button>
                            <button className="btn btn-danger" style={{ marginTop: "15px", marginLeft: "7px" }} onClick={handleRemove}>Close</button>
                        </div>
                    )
                }
            </div >
        </div>
    );
}
