import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Profile(props) {
    const [name, setName] = useState(localStorage.getItem('name') || '');
    const [email, setEmail] = useState(localStorage.getItem('email') || '');
    const [path1, setPath1] = useState(JSON.parse(localStorage.getItem('path1')) || []);
    const [path2, setPath2] = useState(JSON.parse(localStorage.getItem('path2')) || []);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await fetch(`http://localhost:5500/info?username=${props.user}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch user data');
                }
                const data = await response.json();
                setName(data.name);
                setEmail(data.email);
                setPath1(data.path1 || []);
                setPath2(data.path2 || []);
                localStorage.setItem('name', data.name);
                localStorage.setItem('email', data.email);
                localStorage.setItem('path1', JSON.stringify(data.path1 || []));
                localStorage.setItem('path2', JSON.stringify(data.path2 || []));
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };
        fetchUserData();
        document.body.style.backgroundColor = props.mode === "light" ? "#f7f7f7" : "#333";
    }, [props.user, props.mode]);

    const handleDelete = async () => {
        try {
            const response = await fetch(`http://localhost:5500/delete?username=${props.user}`, {
                method: "DELETE"
            })
            if (response.ok) {
                props.loggedout();
                console.log("Account Successfully Deleted");
            }
        }
        catch (error) {
            console.log("Error Deleting an Account", error);
        }
    }

    return (
        <div className="container mt-5" style={{ color: props.mode === "light" ? "#333" : "#f0f0f0" }}>
            <div className="row text-center justify-content-center" style={{ marginTop: "80px", marginBottom: "20px" }}>
                <div className="col-md-8">
                    <div className="card">
                        <div className="card-body" style={{ backgroundColor: props.mode === "light" ? "#f0f0f0" : "#444", color: props.mode === "light" ? "#333" : "#f0f0f0" }}>
                            <h5 className="card-title" style={{ marginLeft: "4px", marginBottom: "20px", color: props.mode === "light" ? "#333" : "#f0f0f0" }}>{name.toUpperCase()}</h5>
                            <button className='btn btn-dark' onClick={props.loggedout} style={{ backgroundColor: "#FFA07A", color: "#242424", borderColor: "white" }}>LogOut</button>
                            <button className='btn btn-dark' onClick={handleDelete} style={{ backgroundColor: "#FFA07A", color: "#242424", borderColor: "white", marginLeft: "10px" }}>Delete Account</button>
                        </div>
                    </div>
                    <div className="card" style={{ marginTop: "20px" }}>
                        <div className="card-body" style={{ backgroundColor: props.mode === "light" ? "#f0f0f0" : "#444", color: props.mode === "light" ? "#333" : "#f0f0f0" }}>
                            <h5 className="card-title">Profile Information</h5>
                            <ul className="list-group list-group-flush">
                                <li className="list-group-item" style={{ backgroundColor: props.mode === "light" ? "#f0f0f0" : "#444", color: props.mode === "light" ? "#333" : "#f0f0f0" }}><strong>Name:</strong> {name}</li>
                                <li className="list-group-item" style={{ backgroundColor: props.mode === "light" ? "#f0f0f0" : "#444", color: props.mode === "light" ? "#333" : "#f0f0f0" }}><strong>Email:</strong> {email}</li>
                            </ul>
                        </div>
                    </div>
                    <div className="card mt-3">
                        <div className="card-body" style={{ backgroundColor: props.mode === "light" ? "#f0f0f0" : "#444", color: props.mode === "light" ? "#333" : "#f0f0f0" }}>
                            <div id="accordionExample" className="accordion">
                                <div className="accordion-item">
                                    <h2 className="accordion-header">
                                        <button className="accordion-button" type="button" style={{ backgroundColor: props.mode === "light" ? "#ddd" : "#555", color: props.mode === "light" ? "#333" : "#f0f0f0" }} data-bs-toggle="collapse" data-bs-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne">
                                            <h6 style={{ margin: "0px" }}>StyleTransfer Activity</h6>
                                        </button>
                                    </h2>
                                    <div id="collapseOne" className="accordion-collapse collapse" data-bs-parent="#accordionExample" style={{ backgroundColor: props.mode === "light" ? "#ddd" : "#555" }}>
                                        <div className="accordion-body">
                                            <div className="image-gallery" style={{ display: "flex", justifyContent: "space-around", flexWrap: "wrap", backgroundColor: props.mode === "light" ? "#ddd" : "#555" }}>
                                                {path1.map((path, index) => (
                                                    <img key={index} src={`http://localhost:5500/${path}`} alt="" style={{ height: "250px", width: "250px", marginBottom: "10px", borderRadius: "10px" }} />
                                                ))}
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div id="accordionExample2" className="accordion">
                                <div className="accordion-item">
                                    <h2 className="accordion-header">
                                        <button className="accordion-button" type="button" style={{ backgroundColor: props.mode === "light" ? "#ddd" : "#555", color: props.mode === "light" ? "#333" : "#f0f0f0" }} data-bs-toggle="collapse" data-bs-target="#collapseTwo" aria-expanded="true" aria-controls="collapseTwo">
                                            <h6>DeepDream Activity</h6>
                                        </button>
                                    </h2>
                                    <div id="collapseTwo" className="accordion-collapse collapse" data-bs-parent="#accordionExample2" style={{ backgroundColor: props.mode === "light" ? "#ddd" : "#555" }}>
                                        <div className="accordion-body">
                                            <div className="image-gallery" style={{ display: "flex", justifyContent: "space-around", flexWrap: "wrap", backgroundColor: props.mode === "light" ? "#ddd" : "#555" }}>
                                                {path2.map((path, index) => (
                                                    <img key={index} src={`http://localhost:5500/${path}`} alt="" style={{ height: "250px", width: "250px", marginBottom: "10px", borderRadius: "10px" }} />
                                                ))}
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
