import React, { useState, useEffect } from 'react';

export default function Profile(props) {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await fetch(`http://localhost:5500/info?username=${props.user}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch user data');
                }
                const data = await response.json();
                setName(data.name);
                setEmail(data.email);
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };
        fetchUserData();
    }, []);
    return (
        <div className="container mt-5">
            <div className="row" style={{ marginTop: "100px" }}>
                <div className="col-md-4">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">{name}</h5>
                            <a href="/" className="btn btn-primary">Edit Profile</a>
                        </div>
                    </div>
                </div>
                <div className="col-md-8">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Profile Information</h5>
                            <ul className="list-group list-group-flush">
                                <li className="list-group-item"><strong>Name:</strong> {name}</li>
                                <li className="list-group-item"><strong>Email:</strong> {email}</li>
                            </ul>
                        </div>
                    </div>
                    <div className="card mt-3">
                        <div className="card-body">
                            <h5 className="card-title">Recent Activity</h5>
                            <ul className="list-group list-group-flush">
                                <li className="list-group-item">Logged in on June 21</li>
                                <li className="list-group-item">Posted a new article</li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
