import React, { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Dream() {
    // const [uploadedImage, setUploadedImage] = useState(null);
    const [Image1, setImage1] = useState(null);
    const [Image2, setImage2] = useState(null);
    const fileInputRef = useRef(null);
    document.title = "Creativity - DreamVortex";
    const handleImageUpload = () => {
        const file1 = Image1;
        const file2 = Image2;

    }
    const handleImageUpload1 = (event) => {
        setImage1(event.target.files[0]);
    };
    const handleImageUpload2 = (event) => {
        setImage2(event.target.files[0]);
    };
    const handleImageDeload1 = () => {
        setImage1(null);
        fileInputRef.current.value = '';
    };
    const handleImageDeload2 = () => {
        setImage2(null);
        fileInputRef.current.value = '';
    };

    return (
        <div className="container" style={{ marginTop: "100px" }}>
            <h1 className="text-center">DreamVortex Image Personalization</h1>
            <p className="text-center" style={{ fontSize: '1.2rem', color: '#555', marginTop: '20px' }}>Upload an image below to start your dream journey!</p>
            <div className="custom-file" style={{ marginLeft: "430px" }}>
                <input
                    type="file" onChange={handleImageUpload1} style={{ marginLeft: "20px", marginRight: "20px" }} ref={fileInputRef} accept='image/*'
                />
                <button className="btn btn-primary" onClick={handleImageDeload1}>Delete</button>
            </div>
            <div className="custom-file" style={{ marginLeft: "430px", marginTop: "10px" }}>
                <input
                    type="file" onChange={handleImageUpload2} style={{ marginLeft: "20px", marginRight: "20px" }} ref={fileInputRef} accept='image/*'
                />
                <button className="btn btn-primary" onClick={handleImageDeload2}>Delete</button>
            </div>
            {/*{
                uploadedImage && (
                    <div className="text-center" style={{ marginTop: "30px" }}>
                        <h3 className="mb-3">Uploaded Image Preview</h3>
                        <img src={uploadedImage} alt="Uploaded" className="img-fluid" style={{ maxWidth: '100%' }} />
                    </div>
                )
            }*/}
        </div >
    );
}
