import React, { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Dream() {
    const [Image1, setImage1] = useState(null);
    const [Image2, setImage2] = useState(null);
    const fileInputRef1 = useRef(null);
    const fileInputRef2 = useRef(null);
    document.title = "Creativity - DreamVortex";

    const handleImageUpload = async (username) => {
        try {
            if (!username || !Image1 || !Image2) {
                console.log('Please provide a username and upload both images');
                return;
            }

            const formData = new FormData();
            formData.append('username', username);
            formData.append('file1', Image1);
            formData.append('file2', Image2);

            const response = await fetch('http://localhost:5500/upload', {
                method: 'POST',
                body: formData
            });

            const result = await response.json();

            if (response.ok) {
                console.log('Images uploaded successfully:', result.message);
                setImage1(null);
                setImage2(null);
            } else {
                console.error('Upload failed:', result.error);
            }
        } catch (error) {
            console.error('Error in handleImageUpload:', error);
        }
    };

    const handleImageUpload1 = (event) => {
        setImage1(event.target.files[0]);
    };

    const handleImageUpload2 = (event) => {
        setImage2(event.target.files[0]);
    };

    const handleImageDeload1 = () => {
        setImage1(null);
        fileInputRef1.current.value = '';
    };

    const handleImageDeload2 = () => {
        setImage2(null);
        fileInputRef2.current.value = '';
    };

    return (
        <div className="container" style={{ marginTop: "100px" }}>
            <h1 className="text-center">DreamVortex Image Personalization</h1>
            <p className="text-center" style={{ fontSize: '1.2rem', color: '#555', marginTop: '20px' }}>Upload two images below to start your dream journey!</p>
            <div className="custom-file" style={{ marginLeft: "430px" }}>
                <input
                    type="file" onChange={handleImageUpload1} style={{ marginLeft: "20px", marginRight: "20px" }} ref={fileInputRef1} accept='image/*'
                />
                <button className="btn btn-primary" onClick={handleImageDeload1}>Delete</button>
            </div>
            <div className="custom-file" style={{ marginLeft: "430px", marginTop: "10px" }}>
                <input
                    type="file" onChange={handleImageUpload2} style={{ marginLeft: "20px", marginRight: "20px" }} ref={fileInputRef2} accept='image/*'
                />
                <button className="btn btn-primary" onClick={handleImageDeload2}>Delete</button>
            </div>
            <div className="text-center" style={{ marginTop: "30px" }}>
                <button className="btn btn-success" onClick={handleImageUpload}>Upload Images</button>
            </div>
        </div>
    );
}
