import React, { useState, useEffect } from 'react';

export default function Dalle() {
    const [inputText, setInputText] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [image, setImage] = useState(null);

    useEffect(() => {
        document.title = "Generator - DreamVortex";
    }, []);

    const handleInputChange = (event) => {

    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        setLoading(true);
        setError(null);
        setImage(null);
        try {
            const response = await fetch('http://localhost:5500/imageconv', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ text: inputText })
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const data = await response.json();

            if (data.image) {
                setImage(data.image);
            } else {
                setError(data.error);
                console.log(data.error);
            }
        } catch (error) {
            setError('Fetch error:', error.message);
            console.error('Fetch error:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container" style={{ padding: '20px', maxWidth: '600px', marginTop: "70px" }}>
            <h1 className='text-center'>DreamVortex Image Generator</h1>
            <p className='text-center' style={{ fontSize: '1.2rem', color: '#555', marginTop: '20px' }}>
                Welcome to the Dalle Text Input page! Enter some text in the input field below and click the submit button
                to perform actions with the text.
            </p>
            <form onSubmit={handleSubmit}>
                <div className="form-group text-center">
                    <label htmlFor="textInput" style={{ fontSize: '1.2rem', color: '#555', marginTop: '20px', marginBottom: "10px" }}>Enter Text:</label>
                    <input
                        type="text"
                        id="textInput"
                        value={inputText}
                        onChange={handleInputChange}
                        placeholder="Type your text here"
                        className="form-control"
                    />
                    <button type="submit" style={{ marginTop: "15px" }} className="btn btn-success" disabled={loading}>Submit</button>
                </div>
                {loading && <p>Loading...</p>}
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </form>
            {image && (
                <div className="text-center" style={{ marginTop: '20px' }}>
                    <h2>Generated Image:</h2>
                    <img src={image} alt="Generated" style={{ maxWidth: '100%', height: 'auto' }} />
                </div>
            )}
        </div>
    );
}
