import React, { useState, useRef } from 'react';
import 'tailwindcss/tailwind.css';

export default function Dream() {
    const [Image, setImage] = useState(null);
    const [preview, setPreview] = useState(null);
    const [final, setFinal] = useState(null);
    const [spin, setSpin] = useState(false);
    const [alert, setAlert] = useState("");
    const fileInputRef = useRef(null);
    document.title = "Creativity - DreamVortex";

    const ImageUpload = async () => {
        try {
            setSpin(true);
            if (!Image) {
                setAlert("Please Upload the Image");
                setTimeout(() => {
                    setAlert("");
                }, 2000);
                setSpin(false);
                return;
            }
            const formData = new FormData();
            formData.append('file', Image);

            const response = await fetch('http://localhost:5500/dd', {
                method: 'POST',
                body: formData
            });

            const result = await response.json();

            if (response.ok) {
                setImage(null);
                setFinal(result.imageUrl);
                setSpin(false);
                fileInputRef.current.value = '';
                setPreview(null);
            } else {
                setSpin(false);
                console.error('Upload failed:', result.error);
            }
        } catch (error) {
            setSpin(false);
            console.error('Error:', error);
        }
    };

    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        setImage(file);
        if (file) {
            const objectUrl = URL.createObjectURL(file);
            setPreview(objectUrl);
        } else {
            setPreview(null);
        }
    };

    const handleImageDeload = () => {
        setImage(null);
        setSpin(false);
        setPreview(null);
        fileInputRef.current.value = '';
    };

    const handleRemove = () => {
        setFinal(null);
    };

    const handleDownload = async () => {
        if (final) {
            try {
                const response = await fetch(`http://localhost:5500${final}`);
                const blob = await response.blob();

                const downloadLink = document.createElement('a');
                downloadLink.href = window.URL.createObjectURL(blob);
                downloadLink.download = 'Styled_Image.jpg';
                downloadLink.click();
            } catch (error) {
                console.error('Download error:', error);
            }
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-500 via-pink-500 to-red-500 relative overflow-hidden">
            <div className="absolute inset-0 bg-cover bg-center animate-moving-bg" style={{ backgroundImage: 'url("path/to/your/deepdream-background.jpg")', opacity: 0.3 }}></div>
            <div className="container relative z-10 p-6 bg-white bg-opacity-80 rounded-lg shadow-lg">
                {alert && <div className="alert alert-danger text-center" role="alert">{alert}</div>}
                <h2 className="text-center text-3xl font-bold mb-4">Dreamy Creations through Deep Dream Alchemy</h2>
                <p className="text-center text-lg text-gray-700 mb-6">Upload an image below to start your dream journey!</p>
                <div className="flex justify-center">
                    <div className="custom-file text-center">
                        <label htmlFor="ContentImage" className="text-xl font-bold text-gray-800 block mb-2">ðŸ’¤ Dreaming Image:</label>
                        <input
                            type="file" onChange={handleImageUpload} ref={fileInputRef} accept='image/*'
                            className="form-control"
                        />
                        {
                            preview && (
                                <div className="mt-2 text-center">
                                    <img src={preview} alt="Preview" className="img-thumbnail w-44 h-44 mt-4" />
                                </div>
                            )
                        }
                        <div className="flex justify-center space-x-4 mt-4">
                            {preview && <button className="btn btn-danger" onClick={handleImageDeload}>Delete</button>}
                            {!spin && !final && <button className="btn btn-success" onClick={ImageUpload}>Upload</button>}
                        </div>
                        {!alert && spin && <div className="spinner-border text-success mt-4" role="status"></div>}
                    </div >
                </div >
                {
                    final && (
                        <div className='text-center'>
                            <div className="text-center mt-4">
                                <h3>Stylized Image</h3>
                                <img src={`http://localhost:5500${final}`} alt="Stylized" className="img-thumbnail w-72 mt-4" />
                            </div>
                            <button className="btn btn-success mt-4" onClick={handleDownload}>Download</button>
                            <button className="btn btn-danger mt-4 ml-4" onClick={handleRemove}>Close</button>
                        </div>
                    )
                }
            </div >
        </div >
    );
}
