import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Profile.css'; // Import the CSS file

export default function Profile(props) {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [path1, setPath1] = useState([]);
    const [path2, setPath2] = useState([]);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await fetch(`http://localhost:5500/info?username=${props.user}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch user data');
                }
                const data = await response.json();
                setName(data.name);
                setEmail(data.email);
                setPath1(data.path1 || []);
                setPath2(data.path2 || []);
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };
        fetchUserData();
        document.body.style.backgroundColor = props.mode === "light" ? "#f7f7f7" : "#333";
    }, [props.user, props.mode]);

    const handleDelete = async () => {
        try {
            const response = await fetch(`http://localhost:5500/delete?username=${props.user}`, {
                method: "DELETE"
            });
            if (response.ok) {
                props.loggedout();
                console.log("Account Successfully Deleted");
            }
        } catch (error) {
            console.log("Error Deleting an Account", error);
        }
    };

    return (
        <div className={`container mt-5 profile-container ${props.mode}`}>
            <div className="row text-center justify-content-center mt-5 mb-3">
                <div className="col-md-8">
                    <div className="card profile-card">
                        <div className="card-body">
                            <h5 className="card-title">{name.toUpperCase()}</h5>
                            <div className='btn-group'>
                                <button className='btn btn-custom' onClick={props.loggedout}>LogOut</button>
                                <button className='btn btn-custom' onClick={handleDelete}>Delete Account</button>
                            </div>
                        </div>
                    </div>
                    <div className="card mt-3">
                        <div className="card-body">
                            <h5 className="card-title">Profile Information</h5>
                            <ul className="list-group list-group-flush">
                                <li className="list-group-item"><strong>Name:</strong> {name}</li>
                                <li className="list-group-item"><strong>Email:</strong> {email}</li>
                            </ul>
                        </div>
                    </div>
                    <div className="card mt-3">
                        <div className="card-body">
                            <div id="accordionExample" className="accordion">
                                <div className="accordion-item">
                                    <h2 className="accordion-header">
                                        <button className="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#collapseOne">
                                            <h6>StyleTransfer Activity</h6>
                                        </button>
                                    </h2>
                                    <div id="collapseOne" className="accordion-collapse collapse" data-bs-parent="#accordionExample">
                                        <div className="accordion-body">
                                            <div className="image-gallery">
                                                {path1.map((path, index) => (
                                                    <img key={index} src={`http://localhost:5500/${path}`} alt="" />
                                                ))}
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div id="accordionExample2" className="accordion mt-3">
                                <div className="accordion-item">
                                    <h2 className="accordion-header">
                                        <button className="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#collapseTwo">
                                            <h6>DeepDream Activity</h6>
                                        </button>
                                    </h2>
                                    <div id="collapseTwo" className="accordion-collapse collapse" data-bs-parent="#accordionExample2">
                                        <div className="accordion-body">
                                            <div className="image-gallery">
                                                {path2.map((path, index) => (
                                                    <img key={index} src={`http://localhost:5500/${path}`} alt="" />
                                                ))}
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
