import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Dream() {
    const [uploadedImage, setUploadedImage] = useState(null);

    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setUploadedImage(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    return (
        <div className="container mt-3">
            <h1 className="text-center mb-4">DreamVortex Image Generator</h1>
            <p className="text-center mb-4">Upload an image below to start your dream journey!</p>
            <div className="custom-file mb-4">
                <input type="file" className="custom-file-input" id="dreamImageInput" onChange={handleImageUpload} />
                <label className="custom-file-label" htmlFor="dreamImageInput">Choose image</label>
            </div>
            {uploadedImage && (
                <div className="text-center">
                    <h3 className="mb-3">Uploaded Image Preview</h3>
                    <img src={uploadedImage} alt="Uploaded" className="img-fluid" style={{ maxWidth: '100%' }} />
                </div>
            )}
        </div>
    );
}
