import React, { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './index.css';  // Ensure you import the Tailwind CSS

export default function Dream() {
    const [Image, setImage] = useState(null);
    const [preview, setpreview] = useState(null);
    const [final, setFinal] = useState(null);
    const [spin, setSpin] = useState(false);
    const [alert, setAlert] = useState("");
    const fileInputRef = useRef(null);
    document.title = "Creativity - DreamVortex";

    const ImageUpload = async () => {
        try {
            setSpin(true);
            if (!Image) {
                setAlert("Please Upload the Image");
                setTimeout(() => setAlert(""), 2000);
                setSpin(false);
                return;
            }
            const formData = new FormData();
            formData.append('file', Image);

            const response = await fetch('http://localhost:5500/dd', {
                method: 'POST',
                body: formData
            });

            const result = await response.json();
            if (response.ok) {
                setImage(null);
                setFinal(result.imageUrl);
                setSpin(false);
                fileInputRef.current.value = '';
                setpreview(null);
            } else {
                setSpin(false);
                console.error('Upload failed:', result.error);
            }
        } catch (error) {
            setSpin(false);
            console.error('Error:', error);
        }
    };

    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        setImage(file);
        if (file) {
            const objectUrl = URL.createObjectURL(file);
            setpreview(objectUrl);
        } else {
            setpreview(null);
        }
    };

    const handleImageDeload = () => {
        setImage(null);
        setSpin(false);
        setpreview(null);
        fileInputRef.current.value = '';
    };

    const handleRemove = () => {
        setFinal(null);
    };

    const handleDownload = async () => {
        if (final) {
            try {
                const response = await fetch(`http://localhost:5500${final}`);
                const blob = await response.blob();
                const downloadLink = document.createElement('a');
                downloadLink.href = window.URL.createObjectURL(blob);
                downloadLink.download = 'Styled_Image.jpg';
                downloadLink.click();
            } catch (error) {
                console.error('Download error:', error);
            }
        }
    };

    return (
        <div className="relative flex items-center justify-center min-h-screen bg-gray-800 overflow-hidden">
            <div className="absolute inset-0">
                <div className="bg-gradient-to-r from-purple-400 via-pink-500 to-red-500 animate-move bg-[length:200%_200%]"></div>
            </div>
            <div className="relative z-10 p-10 bg-white bg-opacity-90 rounded-lg shadow-lg">
                {alert && <div className="alert alert-danger text-center mb-4">{alert}</div>}
                <h2 className="text-2xl font-bold text-center mb-4">Dreamy Creations through Deep Dream Alchemy</h2>
                <p className="text-center mb-4">Upload an image below to start your dream journey!</p>
                <div className="text-center mb-4">
                    <label htmlFor="ContentImage" className="block font-semibold mb-2">ðŸ’¤ Dreaming Image:</label>
                    <input
                        type="file"
                        onChange={handleImageUpload}
                        ref={fileInputRef}
                        accept="image/*"
                        className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                    />
                    {preview && (
                        <div className="mt-4">
                            <img src={preview} alt="Preview" className="w-44 h-44 mx-auto rounded-lg shadow-lg" />
                            <button className="btn btn-danger mt-4" onClick={handleImageDeload}>Delete</button>
                        </div>
                    )}
                    {!spin && !final && <button className="btn btn-success mt-4" onClick={ImageUpload}>Upload</button>}
                    {!alert && spin && <div className="spinner-border text-success mt-4" role="status"></div>}
                </div>
                {final && (
                    <div className="text-center">
                        <h3 className="text-xl font-semibold mt-4">Stylized Image</h3>
                        <img src={`http://localhost:5500${final}`} alt="Stylized" className="w-72 mx-auto mt-4 rounded-lg shadow-lg" />
                        <div className="mt-4">
                            <button className="btn btn-success" onClick={handleDownload}>Download</button>
                            <button className="btn btn-danger ml-4" onClick={handleRemove}>Close</button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}
