import React, { useState, useRef, useEffect } from 'react';
import { TiDownload } from "react-icons/ti";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Dream(props) {
    const [Image, setImage] = useState(null);
    const [preview, setPreview] = useState(null);
    const [final, setFinal] = useState(null);
    const [spin, setSpin] = useState(false);
    const [alert, setAlert] = useState("");
    const fileInputRef = useRef(null);

    useEffect(() => {
        document.body.style.backgroundColor = props.mode === "light" ? "#f0f0f0" : "#333";
    }, [props.mode]);

    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        setImage(file);
        if (file) {
            const objectUrl = URL.createObjectURL(file);
            setPreview(objectUrl);
        } else {
            setPreview(null);
        }
    };

    const handleImageDelete = () => {
        setImage(null);
        setSpin(false);
        setPreview(null);
        fileInputRef.current.value = '';
    };

    const handleRemove = () => {
        setFinal(null);
    };

    const handleDownload = async () => {
        if (final) {
            try {
                const response = await fetch(`http://localhost:5500${final}`);
                const blob = await response.blob();

                const downloadLink = document.createElement('a');
                downloadLink.href = window.URL.createObjectURL(blob);
                downloadLink.download = 'Styled_Image.jpg';
                downloadLink.click();
            } catch (error) {
                console.error('Download error:', error);
            }
        }
    };

    const handleImageUploadAndProcess = async () => {
        try {
            setSpin(true);
            if (!Image) {
                setAlert("Please upload an image.");
                setTimeout(() => {
                    setAlert("");
                }, 2000);
                setSpin(false);
                return;
            }

            const formData = new FormData();
            formData.append('file', Image);
            formData.append('username', props.user);

            const response = await fetch('http://localhost:5500/dd', {
                method: 'POST',
                body: formData
            });

            const result = await response.json();

            if (response.ok) {
                setImage(null);
                setFinal(result.imageUrl);
                setSpin(false);
                fileInputRef.current.value = '';
                setPreview(null);
            } else {
                setSpin(false);
                console.error('Upload failed:', result.error);
            }
        } catch (error) {
            setSpin(false);
            console.error('Error:', error);
        }
    };

    return (
        <div className="container mt-5" style={{ marginTop: "900px" }}>
            {alert && <div className="alert alert-danger text-center" role="alert" style={{ height: "50px", backgroundColor: "#f8d7da", color: "#721c24", border: "1px solid #f5c6cb", borderRadius: "5px" }}>{alert}</div>}
            <h2 className="text-center">Dreamy Creations through Deep Dream Alchemy</h2>
            <p className="text-center" style={{ fontSize: '1.2rem', color: props.mode === "light" ? '#555' : '#ccc', marginTop: '15px' }}>Upload an image below to start your dream journey!</p>
            <div className="d-flex justify-content-center">
                <div className="custom-file text-center" style={{ marginRight: "10px" }}>
                    <label htmlFor="ContentImage" style={{ fontSize: '18px', fontWeight: 'bold', color: props.mode === "light" ? '#333' : '#ccc', display: 'block', marginBottom: "10px", marginRight: "20px" }}>💤 Dreaming Image:</label>
                    <input
                        type="file" onChange={handleImageUpload} style={{ color: props.mode === "light" ? "#007bff" : "#4caf50" }} ref={fileInputRef} accept='image/*'
                        className="form-control"
                    />
                    {preview && (
                        <div className="mt-2 text-center">
                            <img src={preview} alt="Preview" className="img-thumbnail" style={{ width: '175px', height: "175px", marginTop: "10px" }} />
                        </div>
                    )}
                    <div style={{ justifyContent: "space-evenly", display: "flex" }}>
                        {preview && <button className="btn btn-danger" onClick={handleImageDelete} style={{ marginTop: "15px" }}>Delete</button>}
                        {!spin && !final && <button className="btn btn-success" onClick={handleImageUploadAndProcess} style={{ marginTop: "15px" }}>Upload & Process</button>}
                        {spin && <div className="spinner-border text-success text-center" style={{ marginTop: "20px" }} role="status"></div>}
                    </div>
                </div>
            </div>
            {final && (
                <div className='text-center mt-4'>
                    <h3>Stylized Image</h3>
                    <img src={`http://localhost:5500${final}`} alt="Stylized" className="img-thumbnail" style={{ width: '300px' }} />
                    <button className="btn btn-success" style={{ marginTop: "15px" }} onClick={handleDownload}><TiDownload style={{ marginBottom: "4px" }} /> Download</button>
                    <button className="btn btn-danger" style={{ marginTop: "15px", marginLeft: "7px" }} onClick={handleRemove}>Close</button>
                </div>
            )}
        </div>
    );
}
