import React, { useState, useRef, useEffect } from 'react';
import { TiDownload } from "react-icons/ti";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Neural(props) {
    const [Image1, setImage1] = useState(null);
    const [Image2, setImage2] = useState(null);
    const [preview1, setPreview1] = useState(null);
    const [preview2, setPreview2] = useState(null);
    const [final, setFinal] = useState(null);
    const [spin, setSpin] = useState(false);
    const [alert, setAlert] = useState("");
    const fileInputRef1 = useRef(null);
    const fileInputRef2 = useRef(null);

    useEffect(() => {
        document.body.style.backgroundColor = props.mode === "light" ? "#f2f2f2" : "#333";
    }, [props.mode]);

    const handleImageUpload1 = (event) => {
        const file = event.target.files[0];
        setImage1(file);
        if (file) {
            const objectUrl = URL.createObjectURL(file);
            setPreview1(objectUrl);
        } else {
            setPreview1(null);
        }
    };

    const handleImageUpload2 = (event) => {
        const file = event.target.files[0];
        setImage2(file);
        if (file) {
            const objectUrl = URL.createObjectURL(file);
            setPreview2(objectUrl);
        } else {
            setPreview2(null);
        }
    };

    const handleImageDeload1 = () => {
        setImage1(null);
        setSpin(false);
        setPreview1(null);
        fileInputRef1.current.value = '';
    };

    const handleImageDeload2 = () => {
        setImage2(null);
        setSpin(false);
        setPreview2(null);
        fileInputRef2.current.value = '';
    };

    const handleRemove = () => {
        setFinal(null);
    };

    const handleDownload = async () => {
        if (final) {
            try {
                const response = await fetch(`http://localhost:5500${final}`);
                const blob = await response.blob();

                const downloadLink = document.createElement('a');
                downloadLink.href = window.URL.createObjectURL(blob);
                downloadLink.download = 'Styled_Image.jpg';
                downloadLink.click();
            } catch (error) {
                console.error('Download error:', error);
            }
        }
    };

    const handleImageUpload = async () => {
        try {
            setSpin(true);
            if (!Image1 || !Image2) {
                console.log('Please upload both images');
                setAlert("Please Upload the Images");
                setTimeout(() => {
                    setAlert("");
                }, 2000);
                setSpin(false);
                return;
            }
            const formData = new FormData();
            formData.append('file1', Image1);
            formData.append('file2', Image2);
            formData.append('username', props.user);

            const response = await fetch('http://localhost:5500/nst', {
                method: 'POST',
                body: formData
            });

            const result = await response.json();

            if (response.ok) {
                console.log('Images uploaded:', result.message);
                setImage1(null);
                setImage2(null);
                setFinal(result.imageUrl);
                setSpin(false);
                fileInputRef1.current.value = '';
                fileInputRef2.current.value = '';
                setPreview1(null);
                setPreview2(null);
            } else {
                console.error('Upload failed:', result.error);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div className="container" style={{ marginTop: "80px", color: props.mode === "light" ? "#333" : "#f0f0f0" }}>
            {alert && <div className="alert alert-danger text-center" role="alert" style={{ height: "50px", backgroundColor: "#f8d7da", color: "#721c24", border: "1px solid #f5c6cb", borderRadius: "5px" }}>{alert}</div>}
            <h2 className="text-center">Crafting Unique Visuals with Neural Style Magic</h2>
            <p className="text-center" style={{ fontSize: '1.2rem', color: props.mode === "light" ? "#555" : "#ccc", marginTop: '15px' }}>Upload two images below to start your dream journey!</p>
            <div className="d-flex justify-content-center">
                <div className="custom-file" style={{ marginRight: "10px" }}>
                    <label htmlFor="ContentImage" style={{ fontSize: '18px', fontWeight: 'bold', color: props.mode === "light" ? "#333" : "#ccc", display: 'block', marginBottom: "10px" }}>📸 Content Image:</label>
                    <input
                        type="file" onChange={handleImageUpload1} style={{ color: "green" }} ref={fileInputRef1} accept='image/*'
                        className="form-control"
                    />
                    {
                        preview1 && (
                            <div className="text-center">
                                <img src={preview1} alt="Preview 1" className="img-thumbnail" style={{ width: '175px', height: "175px", marginTop: "20px", marginBottom: "15px" }} />
                                <div><button className="btn btn-danger" onClick={handleImageDeload1}>Delete</button></div>
                            </div>
                        )
                    }
                </div>
                <div className="custom-file" style={{ marginLeft: "10px" }}>
                    <label htmlFor="StyleImage" style={{ fontSize: '18px', fontWeight: 'bold', color: props.mode === "light" ? "#333" : "#ccc", display: 'block', marginBottom: "10px" }}>🎨 Style Image:</label>
                    <input
                        type="file" onChange={handleImageUpload2} style={{ color: "green" }} ref={fileInputRef2} accept='image/*'
                        className="form-control"
                    />
                    {
                        preview2 && (
                            <div className="text-center">
                                <img src={preview2} alt="Preview 2" className="img-thumbnail" style={{ width: '175px', height: "175px", marginTop: "20px", marginBottom: "15px" }} />
                                <div><button className="btn btn-danger" onClick={handleImageDeload2}>Delete</button></div>
                            </div>
                        )
                    }
                </div>
            </div>
            <div className="text-center" style={{ marginTop: "20px" }}>
                <button onClick={handleImageUpload} className="btn btn-success" disabled={spin}>Generate</button>
                {spin && <div className="spinner-border text-primary text-center" style={{ marginTop: "20px" }} role="status"><span className="sr-only">Loading...</span></div>}
                {final && (
                    <div className="text-center" style={{ marginTop: "20px" }}>
                        <h2>Styled Image:</h2>
                        <img src={`http://localhost:5500${final}`} alt="Styled" style={{ maxWidth: '100%', height: 'auto' }} />
                        <div className="text-center" style={{ marginTop: "15px" }}>
                            <button onClick={handleDownload} className="btn btn-success">Download</button>
                            <button onClick={handleRemove} className="btn btn-danger" style={{ marginLeft: "10px" }}>Remove</button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}
