import React, { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../index.css';  // Correct import path to custom CSS

export default function Dream() {
    const [Image, setImage] = useState(null);
    const [preview, setPreview] = useState(null);
    const [final, setFinal] = useState(null);
    const [spin, setSpin] = useState(false);
    const [alert, setAlert] = useState("");
    const fileInputRef = useRef(null);
    document.title = "Creativity - DreamVortex";

    const ImageUpload = async () => {
        try {
            setSpin(true);
            if (!Image) {
                setAlert("Please Upload the Image");
                setTimeout(() => setAlert(""), 2000);
                setSpin(false);
                return;
            }
            const formData = new FormData();
            formData.append('file', Image);

            const response = await fetch('http://localhost:5500/dd', {
                method: 'POST',
                body: formData
            });

            const result = await response.json();
            if (response.ok) {
                setImage(null);
                setFinal(result.imageUrl);
                setSpin(false);
                fileInputRef.current.value = '';
                setPreview(null);
            } else {
                setSpin(false);
                console.error('Upload failed:', result.error);
            }
        } catch (error) {
            setSpin(false);
            console.error('Error:', error);
        }
    };

    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        setImage(file);
        if (file) {
            const objectUrl = URL.createObjectURL(file);
            setPreview(objectUrl);
        } else {
            setPreview(null);
        }
    };

    const handleImageDeload = () => {
        setImage(null);
        setSpin(false);
        setPreview(null);
        fileInputRef.current.value = '';
    };

    const handleRemove = () => {
        setFinal(null);
    };

    const handleDownload = async () => {
        if (final) {
            try {
                const response = await fetch(`http://localhost:5500${final}`);
                const blob = await response.blob();
                const downloadLink = document.createElement('a');
                downloadLink.href = window.URL.createObjectURL(blob);
                downloadLink.download = 'Styled_Image.jpg';
                downloadLink.click();
            } catch (error) {
                console.error('Download error:', error);
            }
        }
    };

    return (
        <div className="container">
            {alert && <div className="alert alert-danger text-center">{alert}</div>}
            <h2 className="text-center">Dreamy Creations through Deep Dream Alchemy</h2>
            <p className="text-center">Upload an image below to start your dream journey!</p>
            <div className="form-container">
                <label htmlFor="ContentImage" className="form-label">ðŸ’¤ Dreaming Image:</label>
                <input
                    type="file"
                    onChange={handleImageUpload}
                    ref={fileInputRef}
                    accept="image/*"
                    className="form-control"
                />
                {preview && (
                    <div className="image-preview">
                        <img src={preview} alt="Preview" />
                        <button className="btn btn-danger mt-2" onClick={handleImageDeload}>Delete</button>
                    </div>
                )}
                {!spin && !final && <button className="btn btn-success mt-2" onClick={ImageUpload}>Upload</button>}
                {!alert && spin && <div className="spinner-border text-success mt-2" role="status"></div>}
            </div>
            {final && (
                <div className="text-center mt-4">
                    <h3>Stylized Image</h3>
                    <img src={`http://localhost:5500${final}`} alt="Stylized" className="stylized-image" />
                    <button className="btn btn-success mt-2" onClick={handleDownload}>Download</button>
                    <button className="btn btn-danger mt-2 ml-2" onClick={handleRemove}>Close</button>
                </div>
            )}
        </div>
    );
}
